def codon(RNA):
#http://bioweb.uwlax.edu/genweb/molecular/seq_anal/translation/translation.html
    codons=[]
    while RNA!='':
        codons.append(RNA[0:3])
        RNA=RNA[3:]
    #print(codons)
    return codons

